// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract ReiToken is Initializable, ERC20Upgradeable {

    function initialize(string memory name, string memory symbol) public initializer {
        __ERC20_init("ReiToken","REI");
    }

    function mint(address to, uint256 amount) public {
        _mint(to, amount);
    }

    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        return super.approve(spender, amount);
    }
}

contracts/TokenAirdrop.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

contract TokenAirdrop is Initializable, OwnableUpgradeable, UUPSUpgradeable {
    ERC20Upgradeable public token;

    mapping(address => uint8) private _whitelist;

    event Whitelisted(address indexed account, uint8 allowedAmount);
    event TokensDistributed(address indexed to, uint8 amount);

    function initialize(ERC20Upgradeable _token) public initializer {
        __Ownable_init(0x6c25971cd92459AF4dB23184f97e4578DfE90F88); // Initialize the Ownable contract
        token = _token;
    }

    function setWhiteList(address[] calldata addresses, uint8 numAllowedToMint) external onlyOwner {
        for (uint256 i = 0; i < addresses.length; i++) {
            _whitelist[addresses[i]] = numAllowedToMint;
            emit Whitelisted(addresses[i], numAllowedToMint);
        }
    }

    function allowedToReceive(address account) external view returns (uint8) {
        return _whitelist[account];
    }

    function distributeTokens(address[] calldata recipients) external onlyOwner {
        for (uint256 i = 0; i < recipients.length; i++) {
            uint8 amount = _whitelist[recipients[i]];
            require(amount > 0, "Recipient is not whitelisted or has already received tokens");

            _whitelist[recipients[i]] = 0;  // Mark the tokens as distributed

            require(token.transferFrom(msg.sender, recipients[i], 10), "Transfer of tokens failed");

            emit TokensDistributed(recipients[i], 10);
        }
    }

    function _authorizeUpgrade(address) internal override onlyOwner {}
}

